const fs = require('fs');
function formatReport(reportPath) {
  if (!fs.existsSync(reportPath)) return "âš  No report available.";
  try {
    const parsed = JSON.parse(fs.readFileSync(reportPath, 'utf8'));
    return parsed.Results && parsed.Results.length > 0
      ? parsed.Results.map(r => `### ğŸ” ${r.Target}\n` +
          (r.Vulnerabilities ? r.Vulnerabilities.map(v =>
            `- **${v.Severity}**: ${v.Title} (${v.VulnerabilityID})\n`
          ).join("") : "âœ… No vulnerabilities found.")
        ).join("\n")
      : "âœ… No vulnerabilities found.";
  } catch (err) {
    return "âŒ Error parsing report.";
  }
}
const prComment = `### ğŸ” Trivy Security Scan Report\n\n` +
                  `#### ğŸ“ **Source Code Scan**\n${formatReport('trivy-source-report.json')}\n\n` +
                  `#### ğŸ³ **Docker Image Scan**\n${formatReport('trivy-docker-report.json')}`;
github.rest.issues.createComment({
  issue_number: context.payload.pull_request.number,
  owner: context.repo.owner,
  repo: context.repo.repo,
  body: prComment.substring(0, 64000) // GitHub max comment size
});
